import React, { useState } from "react";
import { validateForm } from "./Extras";

function AddTransactionForm({ newData }) {
	const [formData, setFormData] = useState({
		date: "",
		description: "",
		category: "",
		amount: "",
	});
	function handleSubmit(e) {
		e.preventDefault();
		//check for validity of form data
		const formInput = validateForm(formData);

		if (formInput.containsEmptyInput) {
			alert("Please fill in all inputs");
		} else if (formInput.hasInvalidDate) {
			alert(
				"Please enter valid date\nYear is expected to be between 2000 and today"
			);
		} else if (!formInput.isInvalid) {
			// persist transaction on server after validations
			fetch("http://localhost:8000/transactions", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify(formData),
			})
				.then((r) => r.json())
				.then((data) => newData(data));
			alert("Transaction Added successfully");
		}
		setFormData("");
	}

	function handleChange(e) {
		e.preventDefault();
		const key = e.target.name;
		const value = e.target.value;
		setFormData({ ...formData, [key]: value });
		console.log(formData);
	}
	return (
		<div className="">
			<form onSubmit={handleSubmit} className="hi">
				<div className="">
					<input type="date" name="date" onChange={handleChange} />
					<input
						type="text"
						name="description"
						placeholder="Description"
						value={formData.description}
						onChange={handleChange}
					/>
					<input
						type="text"
						name="category"
						placeholder="Category"
						value={formData.category}
						onChange={handleChange}
					/>
					<input
						type="number"
						name="amount"
						placeholder="Amount"
						step="0.01"
						value={formData.amount}
						onChange={handleChange}
					/>
				</div>
				<button className="ui button" type="submit">
					Add Transaction
				</button>
			</form>
		</div>
	);
}

export default AddTransactionForm;
